@app.get(&quot;/mediaplans&quot;, response_model=List[Mediaplan])
def read_mediaplans():
    k8s_custom_api = kubernetes.client.CustomObjectsApi()
    return k8s_custom_api.list_namespaced_custom_object(
        API_GROUP, API_VERSION, &quot;default&quot;, CRD_NAME)[&quot;items&quot;]


@app.get(&quot;/mediaplans/{mediaplan_name}&quot;, response_model=Mediaplan)
def read_mediaplan(mediaplan_name: str):
    k8s_custom_api = kubernetes.client.CustomObjectsApi()
    return k8s_custom_api.get_namespaced_custom_object(
        API_GROUP, API_VERSION, &quot;default&quot;, CRD_NAME, mediaplan_name)


@app.post(&quot;/mediaplans&quot;, response_model=Mediaplan)
def create_mediaplan(mediaplan: Mediaplan):
    k8s_custom_api = kubernetes.client.CustomObjectsApi()
    return k8s_custom_api.create_namespaced_custom_object(
        API_GROUP, API_VERSION, &quot;default&quot;, CRD_NAME, mediaplan.dict())


@app.put(&quot;/mediaplans/{mediaplan_name}&quot;, response_model=Mediaplan)
def update_mediaplan(mediaplan_name: str, mediaplan: Mediaplan):
    k8s_custom_api = kubernetes.client.CustomObjectsApi()
    return k8s_custom_api.patch_namespaced_custom_object(
        API_GROUP, API_VERSION, &quot;default&quot;, CRD_NAME, mediaplan_name, mediaplan.dict())


@app.delete(&quot;/mediaplans/{mediaplan_name}&quot;)
def delete_mediaplan(mediaplan_name: str):
    k8s_custom_api = kubernetes.client.CustomObjectsApi()
    return k8s_custom_api.delete_namespaced_custom_object(
        API_GROUP, API_VERSION, &quot;default&quot;, CRD_NAME, mediaplan_name)
</code></pre>
