       return &#x27;sell&#x27;<br/><br/>    # Bullish pattern<br/>    elif (open&lt;close and<br/>    previous_open&gt;previous_close and<br/>    close&gt;previous_open and<br/>    open&lt;=previous_close):<br/>        return &#x27;buy&#x27;<br/><br/>    # No clear pattern<br/>    else:<br/>        return &quot;&quot;<br/><br/>df = get_klines(&#x27;BTCUSDT&#x27;, &#x27;1m&#x27;, 44640)<br/>def order_execution(symbol, signal, max_trade_quantity_percentage, leverage):<br/>    max_trade_quantity = None<br/>    account_balance = binance_futures.fetch_balance()<br/>    usdt_balance = 0<br/>    for b in account_balance[&#x27;assets&#x27;]:<br/>        if b[&#x27;asset&#x27;] == &#x27;USDT&#x27;:<br/>            usdt_balance = float(b[&#x27;balance&#x27;])<br/>            max_trade_quantity = usdt_balance * max_trade_quantity_percentage/100<br/><br/>    # Close long position if signal is opposite<br/>    long_position = None<br/>    short_position = None<br/><br/>    # Get current positions<br/>    positions = binance_futures.fapiPrivateGetPositionRisk()<br/>    for p in positions:<br/>        if p[&#x27;symbol&#x27;] == symbol and p[&#x27;positionSide&#x27;] == &#x27;LONG&#x27;:<br/>            long_position = p<br/>        elif p[&#x27;symbol&#x27;] == symbol and p[&#x27;positionSide&#x27;] == &#x27;SHORT&#x27;:<br/>            short_position = p<br/><br/>    # Close positions<br/>    if long_position is not None:<br/>        binance_futures.fapiPrivatePostOrder(symbol=symbol, side=&#x27;SELL&#x27;, type=&#x27;MARKET&#x27;, quantity=long_position[&#x27;positionAmt&#x27;], positionSide=&#x27;LONG&#x27;, reduceOnly=True)<br/>        time.sleep(1)<br/>    if short_position is not None:<br/>        binance_futures.fapiPrivatePostOrder(symbol=symbol, side=&#x27;BUY&#x27;, type=&#x27;MARKET&#x27;, quantity=short_position[&#x27;positionAmt&#x27;], positionSide=&#x27;SHORT&#x27;, reduceOnly=True)<br/>        time.sleep(1)<br/>        print(&quot;Both positions closed.&quot;)<br/><br/>    # Place new order<br/>    quantity = max_trade_quantity<br/>    if signal == &#x27;buy&#x27;:<br/>        position_side = &#x27;BOTH&#x27;<br/>        opposite_position = short_position<br/>    elif signal == &#x27;sell&#x27;:<br/>        position_side = &#x27;BOTH&#x27;<br/>        opposite_position = long_position<br/>    else:<br/>        print(&quot;Invalid signal. No order placed.&quot;)<br/>        return<br/><br/>    if opposite_position is not None:<br/>        quantity = min(max_trade_quantity, abs(float(opposite_position[&#x27;positionAmt&#x27;])))<br/><br/>    price = None<br/>    if signal == &#x27;buy&#x27;:<br/>        order_type = &#x27;TAKE_PROFIT_MARKET&#x27;<br/>        price = binance_futures.fapiPublicGetTickerPrice({&#x27;symbol&#x27;: symbol})[&#x27;price&#x27;]<br/>    elif signal == &#x27;sell&#x27;:<br/>        order_type = &#x27;STOP_MARKET&#x27;<br/>        price = binance_futures.fapiPublicGetTickerPrice({&#x27;symbol&#x27;: symbol})[&#x27;price&#x27;]<br/>        stop_price = price * (1 + STOP_LOSS_PERCENTAGE / 100)<br/><br/>    order = binance_futures.fapiPrivatePostOrder(symbol=symbol, side=&#x27;BUY&#x27; if signal == &#x27;buy&#x27; else &#x27;SELL&#x27;, type=order_type, quantity=quantity, price=price, stopPrice=stop_price, reduceOnly=False, positionSide=position_side, timeInForce=&#x27;GTC&#x27;, leverage=str(leverage))<br/>    order_id = order[&#x27;orderId&#x27;]<br/>    print(f&quot;Placed {signal} order with order ID {order_id} and quantity {quantity}&quot;)<br/>    time.sleep(1)<br/><br/>    # Set stop loss and take profit orders<br/>    # Get the order details to determine the order price<br/>    if signal == &#x27;buy&#x27;:<br/>        take_profit_price = price * (1 + TAKE_PROFIT_PERCENTAGE / 100)<br/>        stop_loss_price = price * (1 - STOP_LOSS_PERCENTAGE / 100)<br/>    elif signal == &#x27;sell&#x27;:<br/>        take_profit_price = price * (1 - TAKE_PROFIT_PERCENTAGE / 100)<br/>        stop_loss_price = price * (1 + STOP_LOSS_PERCENTAGE / 100)<br/><br/>    # Set stop loss and take profit orders<br/>    binance_futures.fapiPrivatePostOrder(symbol=symbol, side=&#x27;SELL&#x27; if signal == &#x27;buy&#x27; else &#x27;BUY&#x27;, type=&#x27;TAKE_PROFIT_MARKET&#x27;, quantity=quantity, price=take_profit_price, reduceOnly=True, positionSide=position_side, timeInForce=&#x27;GTC&#x27;, stopPrice=None)<br/>    time.sleep(1)<br/>    binance_futures.fapiPrivatePostOrder(symbol=symbol, side=&#x27;SELL&#x27; if signal == &#x27;buy&#x27; else &#x27;BUY&#x27;, type=&#x27;STOP_MARKET&#x27;, quantity=quantity, price=stop_loss_price, reduceOnly=True, positionSide=position_side, timeInForce=&#x27;GTC&#x27;, stopPrice=None)<br/><br/>    # Print order creation confirmation messages<br/>    print(f&quot;Placed stop loss order with stop loss price {stop_loss_price}&quot;)<br/>    print(f&quot;Placed take profit order with take profit price {take_profit_price}&quot;)<br/>    time.sleep(1)<br/>while True:<br/>    df = get_klines(&#x27;BTCUSDT&#x27;, &#x27;1m&#x27;, 44640)<br/>    if df is not None:<br/>        signal = signal_generator(df)<br/>        if signal:<br/>            print(f&quot;The signal time is: {dt.datetime.now().strftime(&#x27;%Y-%m-%d %H:%M:%S&#x27;)} :{signal}&quot;)<br/>            order_execution(&#x27;BTCUSDT&#x27;, signal, MAX_TRADE_QUANTITY_PERCENTAGE, leverage)    <br/>            df = get_klines(&#x27;BTCUSDT&#x27;, &#x27;1m&#x27;, 44640)<br/>        if df is not None:<br/>            signal = signal_generator(df)<br/>            if signal:<br/>                print(f&quot;The signal time is: {dt.datetime.now().strftime(&#x27;%Y-%m-%d %H:%M:%S&#x27;)} :{signal}&quot;)<br/>                order_execution(&#x27;BTCUSDT&#x27;, signal, MAX_TRADE_QUANTITY_PERCENTAGE, leverage)<br/>                time.sleep(0.1)<br/>But I getting ERROR: Traceback (most recent call last):<br/>  File &quot;C:\Users\Alan\AppData\Roaming\Python\Python311\site-packages\ccxt\base\exchange.py&quo