&lt;script&gt;
const canvas = document.getElementById('canvas');
const gridSize = 10;
const colorPalette = {
  '003': 'yellow',
  '004': 'black'
};

function renderCanvas() {
  canvas.innerHTML = '';
  const encodedDataInput = document.getElementById('encodedData').value;
  const encodedQuadrantData = encodedDataInput.replace(/[\s,]+/g, '').match(/.{1,8}/g);

  for (let i = 0; i &lt; gridSize * gridSize; i++) {
    const div = document.createElement('div');
    div.classList.add('quadrant');
    canvas.appendChild(div);
  }

  const quadrantElements = document.querySelectorAll('.quadrant');

  encodedQuadrantData.forEach(function(dataStr) {
    const X = parseInt(dataStr.substring(0, 2), 10);
    const Y = parseInt(dataStr.substring(2, 4), 10);
    const colorIndex = dataStr.substring(4, 7);
    const opacityIndex = dataStr.substring(7);
    const index = (Y - 1) * gridSize + (X - 1);
    if (colorIndex in colorPalette) {
      quadrantElements[index].style.backgroundColor = colorPalette[colorIndex];
      quadrantElements[index].style.opacity = opacityIndex / 9; // convert the opacity level to fraction
    }
  });
}
