typedef enum bit [1:0] {
  IDLE    = 2'b00,
  READ    = 2'b01,
  WRITE   = 2'b10
} apb_ctrl_t;

// APB transaction definition
typedef struct {
  logic             ack;
  logic             err;
  apb_ctrl_t        ctrl;
  logic [31:0]      addr;
  logic [31:0]      wdata;
  logic [31:0]      rdata;
} apb_transaction_t;

module apb_protocol (
  input         logic               clk,
  input         logic               rst_n,
  input         apb_transaction_t   apb_txn,
  output        apb_transaction_t   rsp_insn_Phase,
  output        apb_transaction_t   rsp_data_Phase
);

  // Internal state
  enum logic [7:0]  { IDLE, ADDR1, ADDR2, READ, WRITE } state;
  logic          [31:0]  reg_addr;
  logic          [31:0]  reg_wdata;
  logic          [31:0]  reg_rdata;

  // De-assert reset on rising edge of clk
  always_ff @(posedge clk) begin
    if (!rst_n)
      state &lt;= IDLE;
    else
      state &lt;= next_state;
  end

  // Transaction state machine
  always_comb begin
      next_state = state;
    rsp_insn_Phase.ack   = 0;
    rsp_data_Phase.ack   = 0;

    case (state)
      IDLE: begin
        if (apb_txn.ctrl == READ || apb_txn.ctrl == WRITE)
          next_state = ADDR1;
      end

      ADDR1: begin
        reg_addr = apb_txn.addr;
        next_state = ADDR2;
      end

      ADDR2: begin
        if (apb_txn.ctrl == READ)
          next_state = READ;
        else if (apb_txn.ctrl == WRITE)
          next_state = WRITE;
      end

      READ: begin
        reg_rdata = read_from_address(reg_addr);
        rsp_insn_Phase.rdata = reg_rdata;
        rsp_insn_Phase.ack = 1;
        next_state = IDLE;
      end

      WRITE: begin
        reg_wdata = apb_txn.wdata;
        write_to_address(reg_addr, reg_wdata);
        rsp_insn_Phase.ack = 1;
        next_state = IDLE;
      end
    endcase
  end

endmodule
