le.loop = true;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;tentacleIsPlaying = true;<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;&emsp;if (!fearSystem.isInsane)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (tentacleIsPlaying)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;foreach (ParticleSystem tentacle in TentaclesFX)<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;tentacleIsPlaying = false;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;tentacle.loop = false;<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleReload()<br/>    {<br/>&emsp;&emsp;//GUN RELOAD<br/>&emsp;&emsp;if (shotgun.activeInHierarchy == true &amp;&amp; !isSettingTrap &amp;&amp; Input.GetKeyDown(KeyCode.R) || Input.GetKeyDown(KeyCode.JoystickButton3))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (!isReloading)<br/>&emsp;&emsp;&emsp;{<br/><br/>&emsp;&emsp;&emsp;&emsp;StartCoroutine(Reload());<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleAmmoUI()<br/>    {<br/>&emsp;&emsp;//BULLET UI STRING<br/>&emsp;&emsp;AmmoText.GetComponent&lt;Text&gt;().text = currentAmmo.ToString() + &quot; / &quot; + maxAmmo;<br/><br/>&emsp;&emsp;//BULLET UI SECTION<br/>&emsp;&emsp;if (currentAmmo &gt; 0)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet1.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet1.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (currentAmmo &gt; 1)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet2.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet2.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (currentAmmo &gt; 2)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet3.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet3.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (currentAmmo &gt; 3)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet4.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet4.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (currentAmmo &gt; 4)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet5.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Bullet5.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (menuScript1.PlayerSelected == 2 || menuScript1.PlayerSelected == 3)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (currentAmmo &gt; 5)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Bullet6.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;else<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Bullet6.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;if (currentAmmo &gt; 6)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Bullet7.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;else<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Bullet7.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;if (currentAmmo &gt; 7)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Bullet8.GetComponent&lt;Image&gt;().enabled = true;<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;else<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Bullet8.GetComponent&lt;Image&gt;().enabled = false;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleAiming()<br/>    {<br/>&emsp;&emsp;//AIMING SECTION&emsp;<br/>&emsp;&emsp;if (Input.GetMouseButton(1) &amp;&amp; !m_Animator.GetCurrentAnimatorStateInfo(1).IsName(&quot;IdleBandage&quot;) &amp;&amp; shotgun.activeInHierarchy == true &amp;&amp; !isSettingTrap)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isAiming&quot;, true);<br/>&emsp;&emsp;&emsp;isAiming = true;<br/>&emsp;&emsp;&emsp;fLook.enabled = false;<br/>&emsp;&emsp;&emsp;if (mainCamera != null)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Ray ray = mainCamera.ViewportPointToRay(Vector3.one * 0.5f);<br/>&emsp;&emsp;&emsp;&emsp;//Debug.DrawRay (ray.origin, ray.direction * maxDistance, Color.magenta, 2f);<br/>&emsp;&emsp;&emsp;&emsp;RaycastHit hit;<br/><br/>&emsp;&emsp;&emsp;&emsp;if (Physics.Raycast(ray.origin, ray.direction, out hit, Mathf.Infinity, layerMask))<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;zombieHealth = hit.collider.GetComponentInParent&lt;DemoEnemyControls&gt;();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;crawlerControls = hit.collider.GetComponentInParent&lt;CrawlerControls&gt;();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;easterEgg = hit.collider.GetComponent&lt;EasterEgg&gt;();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;bool gasoline = hit.transform.gameObject.CompareTag(&quot;Gas&quot;);<br/><br/>&emsp;&emsp;&emsp;&emsp;&emsp;if (Physics.Raycast(ray.origin, ray.direction, out hit, Mathf.Infinity, obstacleMask)) //White Reticle If Obstacles<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;Reticle.SetActive(true);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;RedReticle.SetActive(false);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;&emsp;&emsp;else if (zombieHealth != null || crawlerControls != null || easterEgg != null || gasoline)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{ //Red Reticle If Available Target<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;RedReticle.SetActive(true);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;Reticle.SetActive(false);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;&emsp;else<br/>&emsp;&emsp;&emsp;&emsp;{ //White Reticle If Nothing<br/>&emsp;&emsp;&emsp;&emsp;&emsp;Reticle.SetActive(true);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;RedReticle.SetActive(false);<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isAiming&quot;, false);<br/>&emsp;&emsp;&emsp;isAiming = false;<br/>&emsp;&emsp;&emsp;fLook.enabled = true;<br/>&emsp;&emsp;&emsp;Reticle.SetActive(false);<br/>&emsp;&emsp;&emsp;RedReticle.SetActive(false);<br/><br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleGunFire()<br/>    {<br/>&emsp;&emsp;//GUN FIRE<br/>&emsp;&emsp;if (Input.GetMouseButtonDown(0) &amp;&amp; m_Animator.GetBool(&quot;isAiming&quot;) == true &amp;&amp; Time.time &gt;= nextTimetoFire &amp;&amp; !isReloading &amp;&amp; shotgun.activeInHierarchy == true &amp;&amp; !isSettingTrap)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (currentAmmo &gt;= 1)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;nextTimetoFire = Time.time + 1f / fireRate;<br/>&emsp;&emsp;&emsp;&emsp;//StartCoroutine(Fire());<br/>&emsp;&emsp;&emsp;&emsp;StartCoroutine(Shoot());<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;else<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;MsgUI = FindObjectOfType(typeof(NotificationUI)) as NotificationUI;<br/>&emsp;&emsp;&emsp;&emsp;MsgUI.SendMsg(&quot;No Ammo! Press R to reload.&quot;);<br/>&emsp;&emsp;&emsp;&emsp;AudioClip clip = GunEmpty;<br/>&emsp;&emsp;&emsp;&emsp;weaponAudioSource.PlayOneShot(clip);<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleStatsLock()<br/>    {<br/>&emsp;&emsp;//STATS LOCK<br/>&emsp;&emsp;healthSlider.value = currentHealth;<br/>&emsp;&emsp;staminaSlider.value = currentStamina;<br/>&emsp;&emsp;flashlightSlider.value = currentFlashlight;<br/><br/>&emsp;&emsp;if (currentHealth &gt; Health)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentHealth = Health;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (currentStamina &gt; Stamina)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentStamina = Stamina;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (currentHealth &lt; minStamina)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentStamina = minStamina;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;//AMMO LOCK<br/>&emsp;&emsp;if (currentAmmo &gt; maxAmmo)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentAmmo = maxAmmo;<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleFlashlightPhysics()<br/>    {<br/>&emsp;&emsp;//FLASHLIGHT DOUBLE WALKERS VISION<br/>&emsp;&emsp;if (LightOn)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentHitObjects.Clear();<br/>&emsp;&emsp;&emsp;RaycastHit[] hits = new RaycastHit[1];<br/><br/>&emsp;&emsp;&emsp;int numberOfHits = Physics.SphereCastNonAlloc(FlashlightPoint.transform.position, flashlightRadius, FlashlightPoint.transform.forward, hits, flashlightDistance, layerMask, QueryTriggerInteraction.UseGlobal);<br/>&emsp;&emsp;&emsp;if (numberOfHits &gt;= 1)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;for (int i = 0; i &lt; numberOfHits; i++)<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;currentHitObjects.Add(hits[i].transform.gameObject);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;currentHitDistance = hits[i].distance;<br/><br/>&emsp;&emsp;&emsp;&emsp;&emsp;foreach (GameObject zombie in currentHitObjects)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;ai = hits[i].collider.GetComponentInParent&lt;BreadcrumbAi.Ai&gt;();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;if (ai != null)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;ai.visionDistance = ai.visionDistance * 2f;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleFlashlight()<br/>    {<br/>&emsp;&emsp;//FLASHLIGHT TOGGLE<br/>&emsp;&emsp;if (Input.GetKeyDown(KeyCode.F) || DPadButtons.IsLeft)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;toggleFlashlight();<br/>&emsp;&emsp;&emsp;toggleFlashlightSFX();<br/><br/>&emsp;&emsp;&emsp;if (LightOn)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;LightOn = false;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;else if (!LightOn &amp;&amp; flashlightSlider.value &gt;= 0)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;LightOn = true;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;//FLASHLIGHT CONTROL SECTION<br/>&emsp;&emsp;if (LightOn &amp;&amp; flashlightSlider.value &gt;= 0)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentFlashlight -= Time.deltaTime / flashlightFallRate;<br/>&emsp;&emsp;&emsp;LightSource.intensity = 5f;<br/>&emsp;&emsp;}<br/>&emsp;&emsp;if (LightOn &amp;&amp; flashlightSlider.value &lt;= 75)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;LightSource.intensity = 4f;<br/>&emsp;&emsp;}<br/>&emsp;&emsp;if (LightOn &amp;&amp; flashlightSlider.value &lt;= 50)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;LightSource.intensity = 3;<br/>&emsp;&emsp;}<br/>&emsp;&emsp;if (LightOn &amp;&amp; flashlightSlider.value &lt;= 25)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;LightSource.intensity = 1.5f;<br/>&emsp;&emsp;}<br/>&emsp;&emsp;if (LightOn &amp;&amp; flashlightSlider.value &lt;= 0)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;flashlightSlider.value = 0;<br/>&emsp;&emsp;&emsp;LightSource.intensity = 0f;<br/>&emsp;&emsp;&emsp;currentFlashlight = 0f;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else if (flashlightSlider.value &gt;= maxFlashlight)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;flashlightSlider.value = maxFlashlight;<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleStamina()<br/>    {<br/>&emsp;&emsp;//SHOW&amp;HIDE STAMINA SLIDER<br/>&emsp;&emsp;if (currentStamina &lt; Stamina)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;staminaSlider.gameObject.SetActive(true);<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;staminaSlider.gameObject.SetActive(false);<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;//STAMINA CONTROL SECTION<br/>&emsp;&emsp;bool _isRunning = IsRunning();<br/>&emsp;&emsp;bool _canRun = CanRun();<br/><br/>&emsp;&emsp;if (_isRunning &amp;&amp; _canRun)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentStamina = Mathf.Clamp(currentStamina - (StaminaDecreasePerFrame * Time.deltaTime), 0.0f, Stamina);<br/>&emsp;&emsp;&emsp;StaminaRegenTimer = 0.0f;<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (currentStamina &lt; Stamina)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;if (StaminaRegenTimer &gt;= StaminaTimeToRegen)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;currentStamina = Mathf.Clamp(currentStamina + (StaminaIncreasePerFrame * Time.deltaTime), 0.0f, Stamina);<br/>&emsp;&emsp;&emsp;&emsp;else<br/>&emsp;&emsp;&emsp;&emsp;&emsp;StaminaRegenTimer += Time.deltaTime;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/><br/><br/>&emsp;&emsp;if (!_canRun)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;StartCoroutine(StopRunMotion());<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;MotionControllerMotion walkOnlyMotion = mMotionController.GetMotion(0, &quot;Walk Only Motion&quot;);<br/>&emsp;&emsp;&emsp;walkOnlyMotion._IsEnabled = false;<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;private void HandleFirstPerson()<br/>    {<br/>&emsp;&emsp;//FIRST PERSON FEATURE<br/>&emsp;&emsp;if (Input.GetKeyDown(KeyCode.Tab))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (!_1stPerson)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;camRig.ActivateMotor(5);<br/>&emsp;&emsp;&emsp;&emsp;_1stPerson = true;<br/><br/>&emsp;&emsp;&emsp;&emsp;motionController.SetAnimatorMotionPhase(0, 1130);<br/>&emsp;&emsp;&emsp;&emsp;motionController.ActivateMotion(&quot;Strafe&quot;, 0);<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;else if (_1stPerson)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;camRig.ActivateMotor(6);<br/>&emsp;&emsp;&emsp;&emsp;_1stPerson = false;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;IEnumerator StopRunMotion()<br/>&emsp;{<br/>&emsp;&emsp;m_Animator.SetFloat(&quot;InputMagnitude&quot;, 0.5f, 0.1f, 0.5f);<br/>&emsp;&emsp;m_Animator.SetFloat(&quot;InputMagnitudeAvg&quot;, 0.5f, 1f, 0.5f);<br/>&emsp;&emsp;m_Animator.SetFloat(&quot;InputY&quot;, 0.5f, 1f, 0.5f);<br/>&emsp;&emsp;yield return new WaitForSeconds(0.1f);<br/>&emsp;&emsp;MotionControllerMotion walkOnlyMotion = mMotionController.GetMotion(0, &quot;Walk Only Motion&quot;);<br/>&emsp;&emsp;walkOnlyMotion.IsEnabled = true;<br/>&emsp;}<br/>&emsp;public bool IsRunning()<br/>&emsp;{<br/>&emsp;&emsp;if (Input.GetKey(KeyCode.LeftShift) &amp;&amp; Input.GetKey(KeyCode.W))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;return (true);<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;return (false);<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;public bool CanRun()<br/>&emsp;{<br/>&emsp;&emsp;if (currentStamina &gt; 1f)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;return (true);<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;return (false);<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;/*IEnumerator Slide()<br/>    {<br/><br/>&emsp;&emsp;isSliding = true;<br/>&emsp;&emsp;AnimationClip slide = m_Animator.GetCurrentAnimatorStateInfo(0).IsName(&quot;Slide&quot;);<br/>&emsp;&emsp;actorController.AddForce(actorController._Transform.forward * 10, m_Animator.GetCurrentAnimatorStateInfo(0).length);<br/>&emsp;&emsp;yield return new WaitForSeconds(1);<br/>&emsp;&emsp;Debug.Log(&quot;Sliding&quot;);<br/>&emsp;&emsp;<br/>&emsp;}*/<br/><br/>&emsp;IEnumerator Knife()<br/>&emsp;{<br/><br/>&emsp;&emsp;isSliding = true;<br/>&emsp;&emsp;m_Animator.SetBool(&quot;isSliding&quot;, true);<br/>&emsp;&emsp;fLook.enabled = false;<br/><br/>&emsp;&emsp;AudioClip clip = knifeSound;<br/>&emsp;&emsp;weaponAudioSource.PlayOneShot(clip);<br/><br/>&emsp;&emsp;Collider[] hitEnemies = Physics.OverlapSphere(knifeHitPoint.position, knifeRadius, layerMask);<br/><br/>&emsp;&emsp;foreach (Collider enemies in hitEnemies)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (isSliding &amp;&amp; enemies != null)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;if (enemies.transform.tag == &quot;Easter&quot;)<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;easterEgg = enemies.GetComponent&lt;EasterEgg&gt;();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;easterEgg.DestroyTrain();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;DestroyObject(enemies.transform.gameObject, 8);<br/><br/>&emsp;&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;&emsp;if (enemies.transform.tag == &quot;Body&quot; || enemies.transform.tag == &quot;Enemy&quot;)<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;zombieHealth = enemies.GetComponentInParent&lt;DemoEnemyControls&gt;();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;crawlerControls = enemies.GetComponentInParent&lt;CrawlerControls&gt;();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;if (zombieHealth != null || crawlerControls != null)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;zombieHealth.GotHit(knifeDamage);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;crawlerControls.GotHit(knifeDamage);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;AudioClip hit = knifeHitSound;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;weaponAudioSource.PlayOneShot(hit);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;&emsp;yield return new WaitForSeconds(1f);<br/>&emsp;&emsp;isSliding = false;<br/>&emsp;&emsp;m_Animator.SetBool(&quot;isSliding&quot;, false);<br/><br/>&emsp;&emsp;//mMotionController.enabled = true;<br/>&emsp;&emsp;fLook.enabled = true;<br/><br/>&emsp;}<br/><br/>&emsp;public void KnifeHit()<br/>&emsp;{<br/>&emsp;&emsp;/*RaycastHit hit;<br/>&emsp;&emsp;if (Physics.SphereCast(knifeHitPoint.transform.position, 4f, knifeHitPoint.transform.forward, out hit, 4f, layerMask, QueryTriggerInteraction.UseGlobal))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentHitObject = hit.transform.gameObject;<br/>&emsp;&emsp;&emsp;currentHitDistance = hit.distance;<br/><br/>&emsp;&emsp;&emsp;zombieHealth = hit.collider.GetComponentInParent&lt;DemoEnemyControls&gt;();<br/>&emsp;&emsp;&emsp;easterEgg = hit.collider.GetComponent&lt;EasterEgg&gt;();<br/><br/>&emsp;&emsp;&emsp;if (hit.collider.transform.tag == &quot;Easter&quot;)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;easterEgg.DestroyTrain();<br/>&emsp;&emsp;&emsp;&emsp;DestroyObject(hit.transform.gameObject, 8);<br/><br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;if (zombieHealth != null)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;if (hit.collider.transform.tag == &quot;Body&quot; || hit.collider.transform.tag == &quot;Enemy&quot;)<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;zombieHealth.GotHit(damage);<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentHitDistance = maxDistance;<br/>&emsp;&emsp;&emsp;currentHitObject = null;<br/>&emsp;&emsp;}*/<br/>&emsp;}<br/>&emsp;public IEnumerator CharacterSuicide()<br/>&emsp;{<br/>&emsp;&emsp;if (isSuiciding)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;float timeElapsed = 1f;<br/>&emsp;&emsp;&emsp;float lerpDuration = 14;<br/><br/>&emsp;&emsp;&emsp;float startValue = 0;<br/>&emsp;&emsp;&emsp;float endValue = 1.6f;<br/><br/>&emsp;&emsp;&emsp;fearSystem.currentFear = fearSystem.maxFear; //Keeping Anomaly FX<br/><br/>&emsp;&emsp;&emsp;//DISABLING CAM&amp;INPUT<br/>&emsp;&emsp;&emsp;camRig.enabled = false;<br/>&emsp;&emsp;&emsp;camRig._IsCollisionsEnabled = false;<br/>&emsp;&emsp;&emsp;InputSource.IsEnabled = false;<br/>&emsp;&emsp;&emsp;fLook.enabled = false;<br/>&emsp;&emsp;&emsp;if (_1stPerson)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;camRig.ActivateMotor(6);<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;//DISABLING UI<br/>&emsp;&emsp;&emsp;LightSource.enabled = false;<br/>&emsp;&emsp;&emsp;QuestUI.SetActive(false);<br/>&emsp;&emsp;&emsp;ShotgunUI.SetActive(false);<br/>&emsp;&emsp;&emsp;GasolineUI.SetActive(false);<br/>&emsp;&emsp;&emsp;PlayerVitalsUI.SetActive(false);<br/>&emsp;&emsp;&emsp;Inventory.SetActive(false);<br/>&emsp;&emsp;&emsp;KnifeUI.SetActive(false);<br/><br/>&emsp;&emsp;&emsp;//ON KNEE ANIMATION<br/>&emsp;&emsp;&emsp;motionController.SetAnimatorMotionPhase(0, 3400, true);<br/>&emsp;&emsp;&emsp;Player.tag = &quot;Dead&quot;;<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isSuiciding&quot;, true);<br/>&emsp;&emsp;&emsp;isInjured = false; //Disabling Injured Sounds<br/><br/>&emsp;&emsp;&emsp;//STOP BLOOD<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isInjured&quot;, false);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;50Health&quot;, false);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;100Health&quot;, true);<br/><br/>&emsp;&emsp;&emsp;yield return new WaitForSeconds(5f);<br/><br/>&emsp;&emsp;&emsp;//SUICIDE ANIMATION<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isSuiciding2&quot;, true);<br/>&emsp;&emsp;&emsp;yield return new WaitForSeconds(0.8f);<br/><br/>&emsp;&emsp;&emsp;//PLAY AUDIO<br/>&emsp;&emsp;&emsp;audioSource.PlayOneShot(FireShot);<br/>&emsp;&emsp;&emsp;audioSource.PlayOneShot(DeathTragicMusic);<br/>&emsp;&emsp;&emsp;audioSource.PlayOneShot(headshotSound);<br/>&emsp;&emsp;&emsp;if (HeartbeatAudioSource.isPlaying)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;HeartbeatAudioSource.Stop();<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;//HEADSHOT&amp;FALLING ANIMATION<br/>&emsp;&emsp;&emsp;CharacterHead.SetActive(false);<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isSuiciding3&quot;, true);<br/><br/>&emsp;&emsp;&emsp;//NEW BLOOD FX<br/>&emsp;&emsp;&emsp;while (timeElapsed &lt; lerpDuration)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;camBlood.Blood_On_Screen = Mathf.Lerp(startValue, endValue, timeElapsed / lerpDuration);<br/>&emsp;&emsp;&emsp;&emsp;timeElapsed += Time.deltaTime;<br/>&emsp;&emsp;&emsp;&emsp;yield return null;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;camBlood.Blood_On_Screen = endValue;<br/><br/>&emsp;&emsp;&emsp;//ENABLING GAMEOVER UI + DEATH MUSIC + HEARTBEAT STOP<br/>&emsp;&emsp;&emsp;Gameover.SetActive(true);<br/>&emsp;&emsp;&emsp;Cursor.visible = true;<br/>&emsp;&emsp;&emsp;audioSource.clip = GameOverUIMusic;<br/>&emsp;&emsp;&emsp;audioSource.Play();<br/>&emsp;&emsp;&emsp;audioSource.loop = true;<br/>&emsp;&emsp;&emsp;deathCount++;<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;public IEnumerator CharacterDeath()<br/>&emsp;{<br/>&emsp;&emsp;float timeElapsed = 1f;<br/>&emsp;&emsp;float lerpDuration = 14;<br/><br/>&emsp;&emsp;float startValue = 0;<br/>&emsp;&emsp;float endValue = 1.6f;<br/>&emsp;&emsp;if (isDead)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;//PLAY AUDIO<br/>&emsp;&emsp;&emsp;audioSource.PlayOneShot(DeathSound);<br/>&emsp;&emsp;&emsp;audioSource.PlayOneShot(DeathTragicMusic);<br/><br/>&emsp;&emsp;&emsp;//DISABLING CAM&amp;INPUT<br/>&emsp;&emsp;&emsp;camRig.enabled = false;<br/>&emsp;&emsp;&emsp;camRig._IsCollisionsEnabled = false;<br/>&emsp;&emsp;&emsp;InputSource.IsEnabled = false;<br/>&emsp;&emsp;&emsp;fLook.enabled = false;<br/><br/>&emsp;&emsp;&emsp;if (_1stPerson)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;camRig.ActivateMotor(6);<br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;//DEATH ANIMATION<br/>&emsp;&emsp;&emsp;motionController.SetAnimatorMotionPhase(0, 3375, true);<br/>&emsp;&emsp;&emsp;Player.tag = &quot;Dead&quot;;<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isDead&quot;, true);<br/><br/>&emsp;&emsp;&emsp;//DISABLING UI<br/>&emsp;&emsp;&emsp;LightSource.enabled = false;<br/>&emsp;&emsp;&emsp;QuestUI.SetActive(false);<br/>&emsp;&emsp;&emsp;ShotgunUI.SetActive(false);<br/>&emsp;&emsp;&emsp;GasolineUI.SetActive(false);<br/>&emsp;&emsp;&emsp;PlayerVitalsUI.SetActive(false);<br/>&emsp;&emsp;&emsp;Inventory.SetActive(false);<br/>&emsp;&emsp;&emsp;KnifeUI.SetActive(false);<br/><br/>&emsp;&emsp;&emsp;//STOP BLOOD<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isInjured&quot;, false);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;50Health&quot;, false);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;100Health&quot;, true);<br/><br/>&emsp;&emsp;&emsp;//SEND ZOMBIES ANIMATION<br/>&emsp;&emsp;&emsp;zombies = GameObject.FindGameObjectsWithTag(&quot;Enemy&quot;);<br/>&emsp;&emsp;&emsp;closestZombie = null;<br/>&emsp;&emsp;&emsp;foreach (GameObject zombie in zombies)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;float curDistance;<br/>&emsp;&emsp;&emsp;&emsp;curDistance = Vector3.Distance(transform.position, zombie.transform.position);<br/>&emsp;&emsp;&emsp;&emsp;zombieHealth = zombie.gameObject.transform.GetComponent&lt;DemoEnemyControls&gt;();<br/>&emsp;&emsp;&emsp;&emsp;crawlerControls = zombie.gameObject.transform.GetComponent&lt;CrawlerControls&gt;();<br/><br/>&emsp;&emsp;&emsp;&emsp;if (curDistance &lt; 1f)<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;if (zombieHealth != null)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;zombieHealth.EatPlayer();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;zombieHealth.transform.LookAt(transform);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;transform.LookAt(zombieHealth.transform);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;&emsp;&emsp;if (crawlerControls != null)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;crawlerControls.EatPlayer();<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;crawlerControls.transform.LookAt(transform);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;transform.LookAt(crawlerControls.transform);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;yield return closestZombie;<br/><br/>&emsp;&emsp;&emsp;//NEW BLOOD FX<br/>&emsp;&emsp;&emsp;while (timeElapsed &lt; lerpDuration)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;camBlood.Blood_On_Screen = Mathf.Lerp(startValue, endValue, timeElapsed / lerpDuration);<br/>&emsp;&emsp;&emsp;&emsp;timeElapsed += Time.deltaTime;<br/>&emsp;&emsp;&emsp;&emsp;yield return null;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;camBlood.Blood_On_Screen = endValue;<br/><br/>&emsp;&emsp;&emsp;//ENABLING GAMEOVER UI + DEATH MUSIC<br/>&emsp;&emsp;&emsp;Gameover.SetActive(true);<br/>&emsp;&emsp;&emsp;Cursor.visible = true;<br/>&emsp;&emsp;&emsp;audioSource.clip = GameOverUIMusic;<br/>&emsp;&emsp;&emsp;audioSource.Play();<br/>&emsp;&emsp;&emsp;audioSource.loop = true;<br/>&emsp;&emsp;&emsp;deathCount++;<br/>&emsp;&emsp;&emsp;if (HeartbeatAudioSource.isPlaying)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;HeartbeatAudioSource.Stop();<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/><br/>&emsp;void SwitchState()<br/>&emsp;{<br/>&emsp;&emsp;if (healthSlider.value &lt;= 0 &amp;&amp; !isDead)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;isDead = true;<br/>&emsp;&emsp;&emsp;beautify.sepiaIntensity = 1f;<br/>&emsp;&emsp;&emsp;StartCoroutine(CharacterDeath());<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else if (healthSlider.value &gt; 0 &amp;&amp; healthSlider.value &lt; (Health / 5.5f))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;beautify.sepiaIntensity = 1f;<br/>&emsp;&emsp;&emsp;isInjured = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else if (healthSlider.value &gt; (Health / 5.5f) &amp;&amp; healthSlider.value &lt; (Health / 4))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;beautify.sepiaIntensity = 0.90f;<br/>&emsp;&emsp;&emsp;isInjured = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else if (healthSlider.value &gt; (Health / 4) &amp;&amp; healthSlider.value &lt; (Health / 2))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;beautify.sepiaIntensity = 0.65f;<br/>&emsp;&emsp;&emsp;isInjured = true;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else if (healthSlider.value &gt; (Health / 2) &amp;&amp; healthSlider.value &lt; (Health / 1.3f))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;beautify.sepiaIntensity = 0.35f;<br/>&emsp;&emsp;&emsp;isInjured = false;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;else if (healthSlider.value &gt; (Health / 1.3f))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;beautify.sepiaIntensity = 0f;<br/>&emsp;&emsp;&emsp;isInjured = false;<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (isInjured &amp;&amp; !isDead &amp;&amp; !isSuiciding)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isInjured&quot;, true);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;50Health&quot;, true);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;100Health&quot;, false);<br/><br/><br/>&emsp;&emsp;&emsp;if (Time.time - timePainSoundPlayed &lt; 4f) return;<br/>&emsp;&emsp;&emsp;HeartbeatAudioSource.clip = Heartbeat;<br/>&emsp;&emsp;&emsp;HeartbeatAudioSource.Play();<br/>&emsp;&emsp;&emsp;int n = Random.Range(1, Painsounds.Length);<br/>&emsp;&emsp;&emsp;AudioClip painSounds = Painsounds[n];<br/>&emsp;&emsp;&emsp;if (!audioSource.isPlaying)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;audioSource.PlayOneShot(painSounds);<br/>&emsp;&emsp;&emsp;&emsp;Painsounds[n] = Painsounds[0];<br/>&emsp;&emsp;&emsp;&emsp;Painsounds[0] = painSounds;<br/>&emsp;&emsp;&emsp;&emsp;timePainSoundPlayed = Time.time;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/><br/>&emsp;&emsp;if (!isInjured || isDead || isSuiciding)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;m_Animator.SetBool(&quot;isInjured&quot;, false);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;50Health&quot;, false);<br/>&emsp;&emsp;&emsp;BloodScreenFx.SetBool(&quot;100Health&quot;, true);<br/><br/>&emsp;&emsp;&emsp;HeartbeatAudioSource.clip = Heartbeat;<br/>&emsp;&emsp;&emsp;HeartbeatAudioSource.Stop();<br/>&emsp;&emsp;}<br/>&emsp;}<br/><br/>&emsp;IEnumerator ToggleCrouch()<br/>&emsp;{<br/>&emsp;&emsp;for (int i = 0; i &lt; m_Animator.layerCount; i++)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (m_Animator.GetLayerName(i) == BaseLayer)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;AnimatorStateInfo stateInfo = m_Animator.GetCurrentAnimatorStateInfo(i);<br/><br/>&emsp;&emsp;&emsp;&emsp;if (stateInfo.IsName(&quot;Base Layer.Sneak v2-SM.Move Tree&quot;))<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;isCrouching = true;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;stealthFX.SetBool(&quot;Stealth&quot;, true);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;yield return new WaitForSeconds(2);<br/>&emsp;&emsp;&emsp;&emsp;}<br/><br/><br/>&emsp;&emsp;&emsp;&emsp;if (stateInfo.IsName(&quot;Base Layer.WalkRunPivot v2-SM.Move Tree&quot;))<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;isCrouching = false;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;stealthFX.SetBool(&quot;Stealth&quot;, false);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;yield return new WaitForSeconds(2);<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;&emsp;yield return new WaitForSeconds(2);<br/>&emsp;}<br/>&emsp;public void Bleed(Quaternion rot)<br/>&emsp;{<br/>&emsp;&emsp;GameObject blood = Instantiate(bloodPrefab, transform.position, rot) as GameObject;<br/>&emsp;&emsp;Destroy(blood, 5);<br/>&emsp;}<br/><br/>&emsp;public void TakeDamage(float damage)<br/>&emsp;{<br/>&emsp;&emsp;//PLAY PAIN SOUND<br/>&emsp;&emsp;int n = Random.Range(1, Hitsounds.Length);<br/>&emsp;&emsp;AudioClip clip = Hitsounds[n];<br/>&emsp;&emsp;audioSource.PlayOneShot(clip);<br/><br/>&emsp;&emsp;//DAMAGE<br/>&emsp;&emsp;currentHealth -= damage;<br/><br/>&emsp;&emsp;//StartCoroutine(HitAnimation());<br/><br/>&emsp;}<br/><br/>&emsp;public void OnTriggerStay(Collider other)<br/>&emsp;{<br/>&emsp;&emsp;if (other.gameObject.CompareTag(&quot;Fire&quot;))<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;currentHealth -= Time.deltaTime / 3;<br/>&emsp;&emsp;&emsp;if (!audioSource.isPlaying)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;AudioClip clip = FrySound;<br/>&emsp;&emsp;&emsp;&emsp;audioSource.PlayOneShot(clip);<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}<br/><br/>&emsp;public void OnTriggerEnter(Collider other)<br/>&emsp;{<br/>&emsp;&emsp;/*if (isSliding &amp;&amp; other != null)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;Debug.Log(&quot;Knife Damage&quot;);<br/>&emsp;&emsp;&emsp;Debug.Log(other.name);<br/>&emsp;&emsp;&emsp;if (other.transform.tag == &quot;Easter&quot;)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;easterEgg = other.GetComponent&lt;EasterEgg&gt;();<br/>&emsp;&emsp;&emsp;&emsp;easterEgg.DestroyTrain();<br/>&emsp;&emsp;&emsp;&emsp;DestroyObject(other.transform.gameObject, 8);<br/><br/>&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;&emsp;if (other.transform.tag == &quot;Body&quot; || other.transform.tag == &quot;Enemy&quot;)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;zombieHealth = other.GetComponentInParent&lt;DemoEnemyControls&gt;();<br/>&emsp;&emsp;&emsp;if (zombieHealth != null)<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;zombieHealth.GotHit(knifeDamage);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;Debug.Log(knifeDamage);<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}*/<br/>&emsp;}<br/><br/>&emsp;/*private IEnumerator HitAnimation()<br/>    {<br/>&emsp;&emsp;//GET NEARBY ZOMBIE THAT ATTACKS<br/>&emsp;&emsp;zombies = GameObject.FindGameObjectsWithTag(&quot;Enemy&quot;);<br/><br/>&emsp;&emsp;foreach (GameObject zombie in zombies)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;float curDistance;<br/>&emsp;&emsp;&emsp;curDistance = Vector3.Distance(transform.position, zombie.transform.position);<br/><br/>&emsp;&emsp;&emsp;// Determine which direction to rotate towards<br/>&emsp;&emsp;&emsp;Vector3 targetPosition = zombie.transform.position;<br/>&emsp;&emsp;&emsp;Vector3 targetDirection = transform.position - zombie.transform.position;<br/>&emsp;&emsp;&emsp;Quaternion desiredRotation = Quaternion.LookRotation(targetDirection, transform.up);<br/><br/>&emsp;&emsp;&emsp;//IF ONE IS NEAR<br/>&emsp;&emsp;&emsp;if (curDistance &lt; 1f)<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;//LOOK AT THIS ZOMBIE<br/>&emsp;&emsp;&emsp;&emsp;Debug.Log(&quot;OUCH&quot;);<br/>                Debug.DrawRay(transform.position, targetPosition * maxDistance, Color.blue, 2f);<br/>                zombie.transform.LookAt(transform);<br/>&emsp;&emsp;&emsp;&emsp;transform.LookAt(targetPosition, transform.up);<br/><br/>                //DISABLING CAM&amp;INPUT<br/>                camRig.enabled = false;<br/>&emsp;&emsp;&emsp;&emsp;camRig._IsCollisionsEnabled = false;<br/>&emsp;&emsp;&emsp;&emsp;InputSource.IsEnabled = false;<br/>&emsp;&emsp;&emsp;&emsp;fLook.enabled = false;<br/><br/>&emsp;&emsp;&emsp;&emsp;yield return new WaitForSeconds(1.5f);<br/><br/>&emsp;&emsp;&emsp;&emsp;//ENABLING CAM&amp;INPUT<br/>&emsp;&emsp;&emsp;&emsp;camRig.enabled = true;<br/>&emsp;&emsp;&emsp;&emsp;camRig._IsCollisionsEnabled = true;<br/>&emsp;&emsp;&emsp;&emsp;InputSource.IsEnabled = true;<br/>&emsp;&emsp;&emsp;&emsp;fLook.enabled = true;<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;}*/<br/>&emsp;void toggleFlashlight()<br/>&emsp;{<br/>&emsp;&emsp;if (LightOn)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;LightSource.enabled = false;<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;LightSource.enabled = true;<br/>&emsp;&emsp;}<br/>&emsp;}<br/>&emsp;void toggleFlashlightSFX()<br/>&emsp;{<br/>&emsp;&emsp;if (LightSource.enabled)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;audioSource.clip = TurnOn;<br/>&emsp;&emsp;&emsp;audioSource.Play();<br/>&emsp;&emsp;}<br/>&emsp;&emsp;else<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;audioSource.clip = TurnOff;<br/>&emsp;&emsp;&emsp;audioSource.Play();<br/>&emsp;&emsp;}<br/><br/>&emsp;}<br/><br/>&emsp;private IEnumerator CheckGasoline()<br/>&emsp;{<br/>&emsp;&emsp;//Toggle UI If Gas In Inventory<br/>&emsp;&emsp;for (int i = 0; i &lt; InvManager.MaxItems; i++)<br/>&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;if (InvManager.Slots[i].IsTaken == true) //Checking if there&#x27;s an item in this slot.<br/>&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;Item ItemScript = InvManager.Slots[i].Item.GetComponent&lt;Item&gt;(); //Getting the item script from the items inside the bag.<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp; //ItemScript.Name = PlayerPrefs.GetString(&quot;Name&quot; + i.ToString()); //Loading the item&#x27;s name.<br/>&emsp;&emsp;&emsp;&emsp;if (ItemScript.Name == &quot;Gasoline&quot; &amp;&amp; !isDead) //Checking if the type of the new item matches with another item already in the bag.<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;GasolineUI.SetActive(true);<br/>&emsp;&emsp;&emsp;&emsp;&emsp;gasolineSlider.value = ItemScript.AmountPercent;<br/><br/>&emsp;&emsp;&emsp;&emsp;&emsp;if (gasolineSlider.value &lt;= 0)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;gasolineSlider.value = 0;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;&emsp;&emsp;if (gasolineSlider.value &gt;= ItemScript.AmountPercent)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;gasolineSlider.value = ItemScript.AmountPercent;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;&emsp;&emsp;if (Input.GetKeyDown(KeyCode.Q) &amp;&amp; ItemScript.AmountPercent &gt;= 20 &amp;&amp; !isAiming &amp;&amp; !isReloading)<br/>&emsp;&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;ItemScript.AmountPercent -= 20;<br/>&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;StartCoroutine(SetTrap());<br/>&emsp;&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;&emsp;}<br/><br/>&emsp;&emsp;&emsp;&emsp;else<br/>&emsp;&emsp;&emsp;&emsp;{<br/>&emsp;&emsp;&emsp;&emsp;&emsp;GasolineUI.SetActive(false);<br/>&emsp;&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;&emsp;}<br/>&emsp;&emsp;}<br/>&emsp;&emsp;yield return new WaitForSeconds(1);<br/>&emsp;}<br/><br/>&emsp;private IEnumerator SetTrap()<br/>&emsp;{<br/>&emsp;&emsp;isSettingTrap = true;<br/><br/>&emsp;&emsp;AudioClip clip = GasolineSound;<br/>&emsp;&emsp;audioSource.PlayOneShot(clip);<br/><br/>&emsp;&emsp;shotgun.Set