chNormalization()(conv9)<br/>    relu9 = Activation(&quot;relu&quot;)(bn9)<br/>    conv10 = Conv2D(num_filter * 16, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(relu9)<br/>    bn10 = BatchNormalization()(conv10)<br/>    relu10 = Activation(&quot;relu&quot;)(bn10)<br/>    up1 = UpSampling2D(size=(2, 2), interpolation=&quot;bilinear&quot;)(relu10)<br/>    # Decoder (Done)<br/>    concat1 = concatenate([up1, relu8], axis=-1)  # , axis=3<br/>    conv11 = Conv2D(num_filter * 8, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(concat1)<br/>    bn11 = BatchNormalization()(conv11)<br/>    relu11 = Activation(&quot;relu&quot;)(bn11)<br/>    conv12 = Conv2D(num_filter * 8, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(relu11)<br/>    bn12 = BatchNormalization()(conv12)<br/>    relu12 = Activation(&quot;relu&quot;)(bn12)<br/>    up2 = UpSampling2D(size=(2, 2), interpolation=&quot;bilinear&quot;)(relu12)<br/>    concat2 = concatenate([up2, relu6], axis=-1)  # , axis=3<br/>    conv13 = Conv2D(num_filter * 4, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(concat2)<br/>    bn13 = BatchNormalization()(conv13)<br/>    relu13 = Activation(&quot;relu&quot;)(bn13)<br/>    conv14 = Conv2D(num_filter * 4, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(relu13)<br/>    bn14 = BatchNormalization()(conv14)<br/>    relu14 = Activation(&quot;relu&quot;)(bn14)<br/>    up3 = UpSampling2D(size=(2, 2), interpolation=&quot;bilinear&quot;)(relu14)<br/>    concat3 = concatenate([up3, relu4], axis=-1)  # , axis=3<br/>    conv15 = Conv2D(num_filter * 2, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(concat3)<br/>    bn15 = BatchNormalization()(conv15)<br/>    relu15 = Activation(&quot;relu&quot;)(bn15)<br/>    conv16 = Conv2D(num_filter * 2, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(relu15)<br/>    bn16 = BatchNormalization()(conv16)<br/>    relu16 = Activation(&quot;relu&quot;)(bn16)<br/>    up4 = UpSampling2D(size=(2, 2), interpolation=&quot;bilinear&quot;)(relu16)<br/>    concat4 = concatenate([up4, relu2], axis=-1)  # , axis=3<br/>    conv17 = Conv2D(num_filter * 1, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(concat4)<br/>    bn17 = BatchNormalization()(conv17)<br/>    relu17 = Activation(&quot;relu&quot;)(bn17)<br/>    conv18 = Conv2D(num_filter * 1, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(relu17)<br/>    bn18 = BatchNormalization()(conv18)<br/>    relu18 = Activation(&quot;relu&quot;)(bn18)<br/>    # Segmentation branch<br/>    segmentation_output = Conv2D(1, 1, activation=&quot;sigmoid&quot;, name=&quot;segmentation_output&quot;)(relu18)  # original<br/>    # Classification branch (Not done)<br/>    gap1 = GlobalAveragePooling2D()(relu8)<br/>    gap2 = GlobalAveragePooling2D()(relu10)<br/>    gap3 = GlobalAveragePooling2D()(relu12)<br/>    conv20 = Conv2D(16, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(segmentation_output)<br/>    bn20 = BatchNormalization()(conv20)<br/>    relu20 = Activation(&quot;relu&quot;)(bn20)<br/>    down5 = MaxPooling2D(pool_size=(4, 4), strides=4)(relu20)<br/>    conv21 = Conv2D(32, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(down5)<br/>    bn21 = BatchNormalization()(conv21)<br/>    relu21 = Activation(&quot;relu&quot;)(bn21)<br/>    down6 = MaxPooling2D(pool_size=(4, 4), strides=4)(relu21)<br/>    conv22 = Conv2D(64, 3, activation=&quot;linear&quot;, padding=&quot;same&quot;, strides=1)(down6)<br/>    bn22 = BatchNormalization()(conv22)<br/>    relu22 = Activation(&quot;relu&quot;)(bn22)<br/>    down7 = MaxPooling2D(pool_size=(4, 4), strides=4)(relu22)<br/>    flatten1 = Flatten()(down7)<br/>    concat5 = concatenate([gap1, gap2, gap3, flatten1], axis=-1)<br/>    # FC layers<br/>    fc1 = Dense(1024, activation=&quot;relu&quot;)(concat5)<br/>    dropout1 = Dropout(0.5)(fc1)<br/>    fc2 = Dense(1024, activation=&quot;relu&quot;)(dropout1)<br/>    dropout2 = Dropout(0.5)(fc2)<br/>    classification_output = Dense(num_classes, activation=&quot;softmax&quot;, name=&quot;classification_output&quot;)(dropout2)<br/>    # Define the model<br/>    model = Model(inputs=inputs, outputs=[classification_output, segmentation_output])<br/>    return model<br/><br/>def segmentation_loss(y_true, y_pred):<br/>    y_true = tf.cast(y_true, tf.float32)<br/>    y_pred = tf.cast(y_pred, tf.float32)<br/>    bce_loss = tf.keras.losses.binary_crossentropy(y_true, y_pred)<br/>    smooth = 1e-5<br/>    intersection = tf.reduce_sum(y_true * y_pred)<br/>    union = tf.reduce_sum(y_true) + tf.reduce_sum(y_pred)<br/>    dice_loss = 1.0 - 2.0 * (intersection + smooth) / (union + smooth)<br/>    segmentation_loss = bce_loss + 1 * dice_loss<br/>    return segmentation_loss<br/><br/>def train_model(model, X_train, y_train_class, y_train_seg, X_val, y_val_class, y_val_seg, batch_size, epochs):<br/>    checkpoint = ModelCheckpoint(<br/>        &quot;multitask_best_weights.h5&quot;,<br/>        monitor=&quot;val_classification_output_accuracy&quot;,<br/>        save_best_only=True,<br/>        mode=&quot;max&quot;,<br/>        verbose=1,)<br/>    reduce_lr = ReduceLROnPlateau(<br/>        monitor=&quot;val_classification_output_accuracy&quot;,<br/>        factor=0.3,<br/>        patience=2,<br/>        min_delta=0.001,<br/>        mode=&quot;auto&quot;,<br/>        verbose=1,)<br/>    tensorboard = TensorBoard(log_dir=&quot;logs&quot;)<br/>    model.compile(<br/>        optimizer=Adam(lr=0.001),<br/>        loss={&quot;classification_output&quot;: &quot;categorical_crossentropy&quot;, &quot;segmentation_output&quot;: segmentation_loss},<br/>        metrics={&quot;classification_output&quot;: &quot;accuracy&quot;, &quot;segmentation_output&quot;: &quot;accuracy&quot;},<br/>        loss_weights={&quot;classification_output&quot;: 1, &quot;segmentation_output&quot;: 1},)<br/>    history = model.fit(<br/>        X_train,<br/>        {&quot;classification_output&quot;: y_train_class, &quot;segmentation_output&quot;: y_train_seg},<br/>        validation_data=(X_val, {&quot;classification_output&quot;: y_val_class, &quot;segmentation_output&quot;: y_val_seg}),<br/>        epochs=epochs,<br/>        verbose=1,<br/>        batch_size=batch_size,<br/>        callbacks=[checkpoint, reduce_lr, tensorboard],)<br/>    return history<br/><br/>def evaluate_model(model, X_test, y_test_class, y_test_seg):<br/>    with tf.keras.utils.custom_object_scope({&quot;segmentation_loss&quot;: segmentation_loss}):<br/>        # Load the best model weights<br/>        best_model = load_model(&quot;multitask_best_weights.h5&quot;)<br/>        # Evaluate the model on test data<br/>        test_loss, test_class_loss, test_seg_loss, test_class_acc, test_seg_acc = best_model.evaluate(<br/>            X_test, {&quot;classification_output&quot;: y_test_class, &quot;segmentation_output&quot;: y_test_seg})<br/>        print(&quot;Test Classification Loss:&quot;, test_class_loss)<br/>        print(&quot;Test Segmentation Loss:&quot;, test_seg_loss)<br/>        print(&quot;Test Classification Accuracy:&quot;, test_class_acc)<br/>        print(&quot;Test Segmentation Accuracy:&quot;, test_seg_acc)<br/>        # Evaluate the model on validation data<br/>        val_loss, val_class_loss, val_seg_loss, val_class_acc, val_seg_acc = best_model.evaluate(<br/>            X_val, {&#x27;classification_output&#x27;: y_val_class, &#x27;segmentation_output&#x27;: y_val_seg})<br/>        print(&quot;Validation Classification Loss:&quot;, val_class_loss)<br/>        print(&quot;Validation Segmentation Loss:&quot;, val_seg_loss)<br/>        print(&quot;Validation Classification Accuracy:&quot;, val_class_acc)<br/>        print(&quot;Validation Segmentation Accuracy:&quot;, val_seg_acc)<br/>        # Evaluate the model on training data<br/>        train_loss, train_class_loss, train_seg_loss, train_class_acc, train_seg_acc = best_model.evaluate(X_train, {&#x27;classification_output&#x27;: y_train_class, &#x27;segmentation_output&#x27;: y_train_seg})<br/>        print(&quot;Train Classification Loss:&quot;, train_class_loss)<br/>        print(&quot;Train Segmentation Loss:&quot;, train_seg_loss)<br/>        print(&quot;Train Classification Accuracy:&quot;, train_class_acc)<br/>        print(&quot;Train Segmentation Accuracy:&quot;, train_seg_acc)<br/>        # Return test classification accuracy<br/>        return test_class_acc<br/><br/>def plot_performance(history):<br/>    # Plot classification accuracy<br/>    classification_train_accuracy = history.history[&quot;classification_output_accuracy&quot;]<br/>    classification_val_accuracy = history.history[&quot;val_classification_output_accuracy&quot;]<br/>    plt.figure(figsize=(7, 3))<br/>    plt.plot(classification_train_accuracy, label=&quot;Training Accuracy&quot;)<br/>    plt.plot(classification_val_accuracy, label=&quot;Validation Accuracy&quot;)<br/>    plt.title(&quot;Classification Accuracy&quot;)<br/>    plt.xlabel(&quot;Epochs&quot;)<br/>    plt.ylabel(&quot;Accuracy&quot;)<br/>    plt.legend()<br/>    plt.show()<br/>    # Plot classification loss<br/>    classification_train_loss = history.history[&quot;classification_output_loss&quot;]<br/>    classification_val_loss = history.history[&quot;val_classification_output_loss&quot;]<br/>    plt.figure(figsize=(7, 3))<br/>    plt.plot(classification_train_loss, &quot;b&quot;, label=&quot;Training Loss&quot;)<br/>    plt.plot(classification_val_loss, &quot;r&quot;, label=&quot;Validation Loss&quot;)<br/>    plt.title(&quot;Classification Loss&quot;)<br/>    plt.xlabel(&quot;Epochs&quot;)<br/>    plt.ylabel(&quot;Loss&quot;)<br/>    plt.legend()<br/>    plt.show()<br/>    # Plot segmentation accuracy<br/>    segmentation_train_accuracy = history.history[&quot;segmentation_output_accuracy&quot;]<br/>    segmentation_val_accuracy = history.history[&quot;val_segmentation_output_accuracy&quot;]<br/>    plt.figure(figsize=(7, 3))<br/>    plt.plot(segmentation_train_accuracy, label=&quot;Training Accuracy&quot;)<br/>    plt.plot(segmentation_val_accuracy, label=&quot;Validation Accuracy&quot;)<br/>    plt.title(&quot;Segmentation Accuracy&quot;)<br/>    plt.xlabel(&quot;Epochs&quot;)<br/>    plt.ylabel(&quot;Accuracy&quot;)<br/>    plt.legend()<br/>    plt.show()<br/>    # Plot segmentation loss<br/>    segmentation_train_loss = history.history[&quot;segmentation_output_loss&quot;]<br/>    segmentation_val_loss = history.history[&quot;val_segmentation_output_loss&quot;]<br/>    plt.figure(figsize=(7, 3))<br/>    plt.plot(segmentation_train_loss, &quot;b&quot;, label=&quot;Training Loss&quot;)<br/>    plt.plot(segmentation_val_loss, &quot;r&quot;, label=&quot;Validation Loss&quot;)<br/>    plt.title(&quot;Segmentation Loss&quot;)<br/>    plt.xlabel(&quot;Epochs&quot;)<br/>    plt.ylabel(&quot;Loss&quot;)<br/>    plt.legend()<br/>    plt.show()<br/><br/>def test_model(model, X_test, y_test_class, y_test_seg, labels, num_images=5):<br/>    random_indices = np.random.choice(len(X_test), num_images, replace=False)<br/>    selected_images = X_test[random_indices]<br/>    selected_labels = y_test_class[random_indices]<br/>    selected_masks = y_test_seg[random_indices]<br/>    predictions = model.predict(selected_images)<br/>    predicted_classes = np.argmax(predictions[0], axis=1)<br/>    predicted_masks = predictions[1]<br/><br/>    bg_color = &#x27;#303030&#x27;  # Lighter than black, as per the previous specification<br/>    text_color = &#x27;white&#x27;<br/><br/>    for i in range(num_images):<br/>        plt.figure(figsize=(10, 5))<br/><br/>        plt.subplot(2, 3, 1)<br/>        plt.imshow(selected_images[i])<br/>        plt.title(f&quot;Input Image\nGround Truth Label: {labels[np.argmax(selected_labels[i])]}\nPredicted Label: {labels[predicted_classes[i]]}&quot;, color=text_color)<br/>        plt.axis(&quot;off&quot;)<br/>        plt.gcf().set_facecolor(bg_color)<br/><br/>        plt.subplot(2, 3, 2)<br/>        plt.imshow(selected_masks[i].squeeze(), cmap=&quot;gray&quot;)<br/>        plt.title(&quot;Ground Truth Segmentation Mask&quot;, color=text_color)<br/>        plt.axis(&quot;off&quot;)<br/><br/>        plt.subplot(2, 3, 3)<br/>        plt.imshow(predicted_masks[i].squeeze(), cmap=&quot;gray&quot;)<br/>        plt.title(&quot;Predicted Segmentation Mask&quot;, color=text_color)<br/>        plt.axis(&quot;off&quot;)<br/>        plt.tight_layout()<br/>        plt.show()<br/><br/># preprocess and predict on the selected image<br/>out = Output()<br/>&#x27;&#x27;&#x27;<br/>def predict_image(image_path):<br/>    # Load and preprocess the image<br/>    img = cv2.imread(image_path)<br/>    img = cv2.cvtColor(img, cv2.COLOR_BGR2RGB)  # Convert BGR to RGB<br/>    img_resized = cv2.resize(img, (image_size, image_size))<br/>    img_expanded = np.expand_dims(img_resized, axis=0)<br/>    # Predict using the model<br/>    class_output, seg_output = model.predict(img_expanded)<br/>    # Get the predicted class<br/>    predicted_class_index = np.argmax(class_output)<br/>    predicted_class = labels[predicted_class_index]<br/>    # Clear the previous output<br/>    with out:<br/>        clear_output(wait=True)<br/>        # Visualize the input and segmentation output<br/>        fig, axs = plt.subplots(1, 3, figsize=(15, 5))<br/>        axs[0].imshow(img)<br/>        axs[0].set_title(&quot;Input Image&quot;)<br/>        axs[0].axis(&quot;off&quot;)<br/><br/>        axs[1].imshow(seg_output[0, :, :, 0], cmap=&#x27;gray&#x27;)<br/>        axs[1].set_title(&quot;Segmentation Output&quot;)<br/>        axs[1].axis(&quot;off&quot;)<br/><br/>        class_probabilities = class_output[0]<br/>        x_pos = np.arange(len(labels))<br/>        axs[2].bar(x_pos, class_probabilities, align=&#x27;center&#x27;, alpha=0.7, color=&#x27;blue&#x27;)<br/>        axs[2].s